@mixin clearfix {
  &::after {
    content: '';
    display: table;
    clear: both;
  }
}

@mixin centerAbsolute {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

// Media query manager

/*
xs: 0-600px: Phone 
sm: 600 - 900px: Tablet portrat
md: 900 - 1200px: Tablet landscape
[1200 - 1800px]: normal styling
lg: 1800px +:  Big desktop
*/

// ORDER: Base + typography > general layout + grid > page layout > components

//em (rem doesn't work in certain browsers) in media queries based on browser font.
// screen is best practice if somebody needs to print
@mixin bp($breakpoint) {
  @if $breakpoint == phone {
    @media only screen and (max-width: 37.5em) {
      //600px
      @content;
    }
  }
  @if $breakpoint == tab-port {
    @media only screen and (max-width: 56.25em) {
      //900px
      @content;
    }
  }
  @if $breakpoint == tab-land {
    @media only screen and (max-width: 75em) {
      //1200px
      @content;
    }
  }
  @if $breakpoint == big-desktop {
    @media only screen and (min-width: 112.5em) {
      //1800px
      @content;
    }
  }
}

