$color-grey-dark-3: #333;
$color-white: #fff;
$color-black: #000;

// GRID
$grid-width: 114rem;
$grid-width-sm: 60rem;
$gutter-vertical: 8rem;
$gutter-vertical-sm: 6rem;
$gutter-horizontal: 6rem;

// FONTS
$font-main: 'Luckiest Guy', cursive;
