// FONT SIZES
$default-font-size: 1.6rem;

// BOX SHADOW
$shadow-xs: 0 0.5rem 2rem rgba($color-black, 0.08);
$shadow-sm: 0 0.8rem 2.5rem rgba($color-black, 0.1);
$shadow-md: 0 1.5rem 4rem rgba($color-black, 0.15);
$shadow-lg: 0 2rem 5rem rgba($color-black, 0.2);

// BORDER RADIUS
$border-radius-sm: 0.3rem;
$border-radius-md: 0.6rem;
$border-radius-lg: 1rem;

// PANELS
$panel-height: 15rem;

$panel-height-phone: 10rem;

