.btn {
  &,
  &:link,
  &:visited {
    text-transform: uppercase;
    text-decoration: none;
    padding: 1.5rem 4rem;
    display: inline-block;
    border-radius: 10rem;
    transition: all 0.1s; /* transition: all on original state */
    position: relative;
    font-family: $font-main;
    font-size: 2rem;

    // Change for <button> element
    border: none; //buttons have default border
    cursor: pointed;
  }

  &:hover {
    transform: translateY(-0.3rem);
    box-shadow: 0 1rem 2rem rgba($color-black, 0.2);
    cursor: pointer;

    &::after {
      transform: scaleX(1.4) scaleY(1.6);
      opacity: 0;
    }
  }

  &:active,
  &:focus {
    outline: none;
    transform: translateY(-0.1rem);
    box-shadow: 0 0.5rem 1rem rgba($color-black, 0.2);
  }

  &--white {
    background-color: $color-white;
    color: $color-grey-dark;

    &::after {
      background-color: $color-white;
    }
  }

  &--primary {
    background-color: $color-primary-dark;
    color: white;

    // &::after {
    //   background-color: $color-primary-dark;
    // }
  }

  /* On hover */

  &--animated {
    animation: fadeInBottom 0.8s ease-out 0.7s;
    animation-fill-mode: backwards; /* applies 0% before animation starts */
  }
}

.btn-text {
  &:link,
  &:visited {
    font-size: $default-font-size;
    color: $color-primary;
    display: inline-block;
    text-decoration: none;
    border-bottom: 1px solid $color-primary;
    padding: 3px;
    transition: all 0.2s;
  }

  &:hover {
    transform: translateY(-3px);
    background-color: $color-primary-light;
    color: $color-white;
    box-shadow: 0 1rem 1rem rgba($color-black, 0.2);
  }

  &:active {
    transform: translateY(0);
    box-shadow: 0 0.5rem 0.5rem rgba($color-black, 0.2);
  }
}

