.message {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba($color-black, 0.8);
  z-index: 9999;
  opacity: 0;
  visibility: hidden;

  &--active {
    opacity: 1;
    visibility: visible;
  }

  &__content {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);

    width: 50%;
    padding: 2rem;
    background-color: $color-grey-light-2;
    border: 4px solid $color-primary-light;
    box-shadow: $shadow-md;
    border-radius: $border-radius-md;

    @include bp(phone) {
      width: 90%;
      padding: 1rem;
    }
  }

  &__header {
    font-size: 3rem;
    margin-bottom: 2rem;
    text-align: center;

    &--win {
      color: $color-primary-light;
      font-size: 4rem;
    }

    &--lose {
      color: $color-secondary-dark;
      font-size: 4rem;
    }
  }

  &__selection {
    text-align: center;
    margin-bottom: 3rem;
  }
}

